  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Insert token: 
            <input type='text' /*value={this.state.token} onChange={this.handleChange}*/ />
          </label>
          <input type="submit" value="Submit" />
        </form>
        <label>Using token: {this.state.using}
        </label>
      </div>
    );
  }
}

export default App;
