//import ReactDOM from 'react-dom'
import React from 'react'
//import Issues from './issues.js';
import { Link } from 'react-router-dom';
//import FormIssue from './FormIssue.js';

class IssuesView extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      issues: [],
      users: []
    };
  }

  componentDidMount() {

    this.loadIssues();
    this.loadUsers();
  }

  loadIssues(){
    window.fetch('https://secure-crag-93015.herokuapp.com/issues', {
      // mode: 'no-cors',
      method: 'GET',
      headers: {
        Accept: 'application/json',
      },
      //body: JSON.stringify({body: "desde react"}),
    },
    ).then(response => {
      if (response.ok) {
        response.json().then(json => {
          this.setState({issues: json});
        });
      }
    });
  }

  loadUsers(){
    window.fetch('https://secure-crag-93015.herokuapp.com/users', {
      // mode: 'no-cors',
      method: 'GET',
      headers: {
        Accept: 'application/json',
      },
      //body: JSON.stringify({body: "desde react"}),
    },
    ).then(response => {
      if (response.ok) {
        response.json().then(json => {
          this.setState({users: json});
        });
      }
    });
  }

  render() {

    //ReactDOM.render(<FormIssue />, document.getElementById('root'));
    return (
      <div>
        <Link to="/">
          <button type="button">Back</button>
        </Link>
      <Issues issues={this.state.issues}/>
      </div>
    );
  }

  Issues = ({ issues }) => {
    return (
      <table className="table table-striped" >
        <thead>
          <tr key='tableHeader'>
            <th scope="col">Title</th>
            <th scope="col">Type</th>
            <th scope="col">Priority</th>
            <th scope="col">Status</th>
            <th scope="col">Asigned</th>
            <th scope="col">Creator</th>
            <th scope="col">Created</th>
            <th scope="col">Updated</th>
            <th colSpan="3"></th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => (
          <tr key={issue.id}>
            <td>{issue.Title}</td>
            <td>{issue.Type}</td>
            <td>{issue.Priority}</td>
            <td>{issue.Status}</td>
            <td>{issue.Asigned}</td>
            <td>{issue.Creator}</td>
            <td>{issue.Created}</td>
            <td>{issue.Updated}</td>
            <td onClick={linkToIssue(issue.id)}>View</td>
          </tr>
          ))}
        </tbody>
      </table>
    );
  };
}

export default IssuesView;
