//import ReactDOM from 'react-dom'
import React from 'react'
import './App.css';
import { Link } from 'react-router-dom';
//import Issues from './issues.js';
//import FormIssue from './FormIssue.js';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      users: [],
      token: '',
      using: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {

    //this.loadIssues();
    //this.loadUsers();
  }

  loadUsers(){
    window.fetch('https://secure-crag-93015.herokuapp.com/users', {
      // mode: 'no-cors',
      method: 'GET',
      headers: {
        Accept: 'application/json',
      },
      //body: JSON.stringify({body: "desde react"}),
    },
    ).then(response => {
      if (response.ok) {
        response.json().then(json => {
          this.setState({users: json});
        });
      }
    });
  }

  handleSubmit(event){
    alert('A name was submitted: ' + this.state.token);
    this.setState({using: this.state.token});
    event.preventDefault();
    console.log(this.state.using);
  }

  handleChange(event){
    this.setState({token: event.target.value});
  }


  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Insert token: 
            <input type='text' value={this.state.token} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
        <label>Using token: {this.state.using}</label>
        <br></br>
        <Link to="/issues">
          <button type="button">All issues</button>
        </Link>
      </div>
    );
  }
}

export default App;
