import React, { Component } from 'react';
import './FormIssue.css';
import ReactDOM from 'react-dom'



class FormIssue extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      title: '' ,
      description:'',
      kind:'select',
      type:'select',
      assignee:'select',
      priority:'',
      casa :[],
       user: [],
      names:[],
    };
  }

  myChangeHandler = (event) => {
    this.setState({title: event.target.value});
    this.setState({description: event.target.value});
    this.setState({kind: event.target.value});
    this.setState({priority: event.target.value});
    this.setState({user: event.target.value});
    this.setState({assignee: event.target.value});



  }

  componentDidMount (){
    fetch('https://secure-crag-93015.herokuapp.com/issues', {
      method: 'POST',
      body: JSON.stringify({
          Title: 'ewqewer',
          Description: 'dasdsa',
          Priority:'bug',
          Type : 'trivial',
          Asigned:this.state.user

      }),
      headers: {
          "Content-type": "application/json; charset=UTF-8"
      }
  }).then(response => {
          return response.json()
      }).then(json => {
          this.setState({

              user:json
          });
      });
  }

  componentDidMount() {


       fetch('https://secure-crag-93015.herokuapp.com/users', {
      // mode: 'no-cors',
      method: 'GET',
      headers: {
        Accept: 'application/json',
      },
      //body: JSON.stringify({body: "desde react"}),
    },
    ).then(response => {
      if (response.ok) {
        response.json().then(json => {
          this.setState({user: json});

          for (var i = 0; i < this.state.user.length; i++) {
            (this.state.casa).push(this.state.user[i].first_name,this.state.user[i].toc)
         }
         this.setState({names: this.state.casa});

          console.log(this.state.names)
        });
      }
    });
  }



  render() {

    return (



      <form>
     <h1>Create Issue</h1>   


  <div  class="form-group">
      <p>Enter Title:</p>
      <input    
       class="form-control"
        id="exampleFormControlInput1" 
        name='Title'
        type='text'
        value={this.state.title}
      onChange={event => this.setState({ title: event.target.value })}
      />
      </div>
        <div class="form-group">
    <p>Description:</p>
      <textarea class="form-control" id="exampleFormControlTextarea1" 

      value={this.state.description}
      onChange={event => this.setState({ description: event.target.value })}
    />
    </div>
     <div class="form-group">

        <p>Kind:</p>
     <select   class="form-control" id="exampleFormControlSelect1" value={this.state.kind}
    onChange={event => this.setState({ kind: event.target.value })}>
    <option  value="Bug">Bug</option>
    <option  value="Enhancement">Enhancement</option>
    <option  value="Proposal">Proposal</option>
    <option  value="Task">Task</option>

    </select>

    </div>


 <div class="form-group">

  <p>Type:</p>
     <select class="form-control" id="exampleFormControlSelect1" name="cars"  value={this.state.type}
    onChange={event => this.setState({ type: event.target.value })}>
    <option  value='Trivial'>Trivial</option>
    <option  value='Minor'>Minor</option>
    <option  value='Major'>Major</option>
    <option  value='Critical'>Critical</option>
    <option  value='Blocker'>Blocker</option>
  </select>
  </div>

  <div  class="form-group">
  <p>assignee:</p>
  <select class="form-control" id="exampleFormControlSelect1"    value={this.state.assignee}
    onChange={event => this.setState({ assignee: event.target.value })} >
    {this.state.names.map(task => <option  value={task}>{task}</option>)} 
  </select>

  </div>

  <div class="form-group">
    <label for="exampleFormControlFile1">Example file input</label>
    <input type="file" class="form-control-file" id="exampleFormControlFile1"></input>

  </div>


  <button variant="contained"   onclick="componentpost()">
  Confirmar
  </button>
  <span> </span>
  <button variant="contained">
  Cancelar
  </button>




      </form>
    );
  }
}


ReactDOM.render(<FormIssue />, document.getElementById('root'));
